<?php/** * Attachment template file. * * @package bcj */get_header(); ?>	<div id="primary" class="content-area">		<main id="main" class="site-main" role="main">			<?php if ( have_posts() ) : ?>				<?php /* Start the Loop */ ?>				<?php while ( have_posts() ) : the_post(); ?>					<article>						<header class="entry-header">							<h1 class="entry-title"><?php the_title(); ?></h1>						</header><!-- .entry-header -->						<div class="entry-content">							<div class="image-frame">								<?php echo wp_get_attachment_image( $attachment->ID, 'full' ); ?>							</div>							<p class="wp-caption-text"><?php echo get_the_excerpt( $attachment->ID ); ?></p>						</div><!-- .entry-content -->					</article><!-- #post-## -->				<?php endwhile; ?>			<?php else : ?>				<?php get_template_part( 'content', 'none' ); ?>			<?php endif; ?>		</main><!-- #main -->	</div><!-- #primary --><?php get_footer(); ?>