<?php/** * Frontpage template file. * * @package bcj */get_header(); ?>	<div id="primary" class="content-area">		<main id="main" class="site-main" role="main">			<header class="entry-header">				<h1 class="entry-title"><?php single_category_title(); ?></h1>			</header><!-- .entry-header -->		<ul class='folio'>		<?php if ( have_posts() ) : ?>			<?php /* Start the Loop */ ?>			<?php while ( have_posts() ) : the_post(); ?>			<li class="folio-item">				<div class='image-frame'>					<a href="<?php the_permalink(); ?>" rel="bookmark">						<?php the_post_thumbnail('thumbnail', array('class' => 'folio-image')); ?>					</a>				</div>				<h2 class="folio-title"><a href="<?php the_permalink(); ?>" rel="bookmark"><?php the_title(); ?></a></h2>				<?php $tags_list = get_the_tag_list( '', __( ' &bull; ', 'bcj' ) );					if ( $tags_list ) :				?>					<span class="folio-tags">						<?php printf( __( '%1$s', 'bcj' ), $tags_list ); ?>					</span>				<?php endif; // End if $tags_list ?>			</li><!-- #post-## -->			<?php endwhile; ?>		</ul>			<?php bcj_paging_nav(); ?>		<?php else : ?>			<?php get_template_part( 'content', 'none' ); ?>		<?php endif; ?>		</main><!-- #main -->	</div><!-- #primary --><?php // get_sidebar(); ?><?php get_footer(); ?>